const textarea=document.getElementById('content');const printable=document.getElementById('printable');let timeout=null;let lastSavedContent=textarea.value;function saveContent(){const currentContent=textarea.value;if(lastSavedContent===currentContent){return}const path=window.location.pathname.substring(1);const data=new URLSearchParams();data.append('content',currentContent);fetch(`/save/${path}`,{method:'POST',body:data,keepalive:true}).then(()=>{lastSavedContent=currentContent;updatePrintable(currentContent)})}function updatePrintable(content){while(printable.firstChild){printable.removeChild(printable.firstChild)}printable.appendChild(document.createTextNode(content))}textarea.addEventListener('input',()=>{clearTimeout(timeout);timeout=setTimeout(saveContent,1500)});window.addEventListener('beforeunload',(event)=>{clearTimeout(timeout);saveContent()});textarea.focus();updatePrintable(textarea.value);